function CommentAjax(n){commentSection=$("section[name='Comment']").find("div[name='comment-box']");commentSection.html("LOADING ...");$.ajax({url:"/Presentation/LoadComments",data:{id:n},success:function(n){$(commentSection).html(n)}})}function DisableBtn(n){pre_text=$(n).text();$(n).text("Please wait ...");$(n).addClass("disabled");$(n).attr("disabled","disabled");$(n).attr("pre_text",pre_text)}function EnableBtn(n){pre_text=$(n).attr("pre_text");$(n).text(pre_text);$(n).removeClass("disabled");$(n).removeAttr("disabled")}$("#comment_form").submit(function(n){n.preventDefault();submitBtn=$(this).find("[type='submit']");presentationId=$(this).find("#PresentationId").val();DisableBtn(submitBtn);$.ajax({type:"POST",url:"/Comment/CreateAjax",data:$(this).serialize(),success:function(){EnableBtn(submitBtn);CommentAjax(presentationId)}})});